import React, { useState } from "react";
import "./ExpenseFilter.css";

const ExpensFilter = (props) => {

    const [filter, setFilter] = useState('');

    const filterChangeHandler = (event) => {
        setFilter(event.target.value);
        // console.log(chosenFilter);
        props.onChosenFilter(filter);
    };

  return (
    <div className="expenses-filter">
      <div className="expenses-filter__control">
        <label className="expenses-filter_label">Filter by year</label>
        <select className="expenses-filter_select" onChange={filterChangeHandler}>
          <option value="2022" >2022</option>
          <option value="2021">2021</option>
          <option value="2020">2020</option>
          <option value="2019">2019</option>
        </select>
      </div>
    </div>
  );
};

export default ExpensFilter;
